/// Macro to start attack sequence - shows quick list of weapons/powers for actor and roll main skill
let actoratc = canvas.tokens.controlled[0]?.actor;
if (!actoratc) {
    ui.notifications.warn('NoSelectedActor');
    return;
}

let items = actoratc.items.filter(el => (el.type == "power" || el.type == "weapon"));
if (!items.length) {
    ui.notifications.warn('NoEquippedItemOrPower');
    return;
} 

items.sort((a, b) => a.name.localeCompare(b.name));

let buttons = {}
items.map((item) => {
    buttons[item.id] = {
        label: item.name,
        callback: (html) => { game.brsw.create_item_card(actoratc, item.id, true); }
    }
});

new Dialog({
    title: actoratc.name + ': ' + game.i18n.localize("SWADE.QuickAccess"),
    content: `<label style="font-size:14px;display:flex;align-items:center;margin-bottom: 5px;"></label>`,
    buttons: buttons,
    classes: 'horizontal-dialog'
}, { classes: ["vertical-buttons"] }).render(true);
