command:
new Dialog({
    title: 'Calculate Fall Damage',
    content: `<form>
                <div class="form-group">
                    <label>Distance in feet</label>
                    <input type='number' name='feetField'></input>
                </div>
                <div class="form-group">
                    <label>Distance in table inches</label>
                    <input type='number' name='inchField'></input>
                </div>
            </form>`,
    buttons: {
        confirm: {
            icon: '<i class="fas fa-check"></i>',
            label: 'Confirm',
            callback: async html => {
                let damIncrements = 0;
                let feet = html.find('input[name=\'feetField\']').val();
                if (feet > 0) {
                    damIncrements = Math.min(Math.floor(feet / 10), 10);
                } else {
                    let inch = html.find('input[name=\'inchField\']').val();
                    damIncrements = Math.min(Math.floor(inch / 2), 10);
                }
                let output = damIncrements ? ('<p>' + damIncrements + 'd6 + ' + damIncrements + '<p>') : "0";
                new Dialog({
                    title: 'Fall Damage',
                    content: output,

                    buttons: {
                        ok: {
                            label: "OK"
                        }
                    }
                }).render(true);
            }
        }
    }
}).render(true);
